import React, { useState } from "react";
import { Configuration, OpenAIApi } from "openai";

const configuration = new Configuration({
  apiKey: "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",
});

function handleSpeakButtonClick(text) {
  const synth = window.speechSynthesis;
  const utterance = new SpeechSynthesisUtterance(text);
  synth.speak(utterance);
}

async function runCompletion(transcript) {
  try {
    const openai = new OpenAIApi(configuration);
    const completion = await openai.createCompletion({
      model: "text-davinci-003",
      prompt: transcript,
      max_tokens: 300,
      temperature: 1,
    });
    const generatedText = completion.data.choices[0].text;
    console.log(generatedText);
    handleSpeakButtonClick(generatedText);
  } catch (error) {
    console.error(error);
  }
}

function Mallaya() {
  const [transcript, setTranscript] = useState("");

  const recognition = new window.webkitSpeechRecognition();

  recognition.onresult = function (event) {
    const last = event.results.length - 1;
    const result = event.results[last][0].transcript;
    setTranscript(result);
    runCompletion(result);
  };

  function startListening() {
    recognition.start();
  }

  return (
    <div>
      <div>
      <button onClick={startListening}>Start Listening</button>
      <p>{transcript}</p>
      </div>
    </div>
  );
}

export default Mallaya;
